import React from "react";
import {
  AuthenticationActionTypes,
  useAuthentication,
} from "../../../../context/authentication/Authentication.context";
import {socket} from "../../../../socket/socket";

const LogoutButton = () => {
  const [{name}, dispatch] = useAuthentication();
  return (
    <button
      onClick={() => {
        socket.emit('logout', {name})
        dispatch({ type: AuthenticationActionTypes.logout })
      }}
    >
      Logout
    </button>
  );
};

export { LogoutButton };
