import React, { useLayoutEffect, useRef, useState } from "react";
import { LogoutButton } from "./components/logout-button/logout-button";
import { UserList } from "./components/user-list/UserList";
import { useSubscription } from "../../socket/use-subscription/useSubscription";
import { socket } from "../../socket/socket";
import { useAuthentication } from "../../context/authentication/Authentication.context";

const openMediaDevices = async (constraints: any) => {
  return await navigator.mediaDevices.getUserMedia(constraints);
};

const constraints = { video: true, audio: false };
let stream: any = null;

const configuration = {
  iceServers: [
    {
      urls: "stun:10.68.87.6:3478",
      username: "username",
      credential: "password",
    },
  ],
};
const peerConnection = new RTCPeerConnection(configuration);

const ChatPage = () => {
  const [hasOffer, setHasOffer] = useState(false);
  const ownVideoRef = useRef<any>();
  const foreignVideoRef = useRef<any>();
  const offerRef = useRef<{ from: string | null; offer: any }>({
    from: null,
    offer: null,
  });
  const [isCalling, setIsCalling] = useState(false);
  const [{ name: currentUserName }] = useAuthentication();

  useSubscription("video:answer", async ({ answer, from }) => {
    const remoteDesc = new RTCSessionDescription(answer);
    await peerConnection.setRemoteDescription(remoteDesc);
    setIsCalling(false);
    console.log(answer, from);
  });

  useSubscription("video:offer", async ({ offer, from }) => {
    setHasOffer(true);
    offerRef.current = { offer, from };
    console.log(offer, from);
  });

  const pickUp = async () => {
    if (offerRef.current.offer) {
      // create answer
      const { from, offer } = offerRef.current;
      await peerConnection.setRemoteDescription(
        new RTCSessionDescription(offer)
      );
      const answer = await peerConnection.createAnswer();
      await peerConnection.setLocalDescription(answer);
      socket.emit("video:answer", { answer, to: from, from: currentUserName });
    }
  };

  const hangUp = () => {
    if (offerRef.current) {
      // implement call decline
    }
  };

  const onVideo = async (name: string) => {
    const offer = await peerConnection.createOffer();
    await peerConnection.setLocalDescription(offer);
    setIsCalling(true);
    socket.emit("video:offer", { offer, to: name, from: currentUserName });
  };

  const onAudio = (name: string) => {};

  useLayoutEffect(() => {
    const connect = async () => {
      try {
        stream = await openMediaDevices(constraints);
        ownVideoRef.current.srcObject = stream;
      } catch (e) {
        console.error(e);
      }
    };
    connect();
  }, []);

  return (
    <div>
      <UserList onAudio={onAudio} onVideo={onVideo} />
      {hasOffer && (
        <div>
          <div>CALLING....</div>
          <button onClick={pickUp}>PICK UP</button>
          <button>HANG UP</button>
        </div>
      )}
      {isCalling && (
        <div>
          <div>CALLING...!</div>
        </div>
      )}
      <LogoutButton />

      <video
        id={"ownVideo"}
        ref={ownVideoRef}
        autoPlay
        playsInline
        controls={false}
        style={{
          height: "300px",
          width: "300px",
        }}
      />
      <video
        id={"foreignVideo"}
        ref={foreignVideoRef}
        autoPlay
        playsInline
        controls={false}
        style={{
          height: "300px",
          width: "300px",
        }}
      />
    </div>
  );
};

export { ChatPage };
