import { socket } from "../../socket/socket";

type Callback = (...args: any) => void;

class EventListenerContainer {
  listeners: { [event: string]: Callback[] } = {};
  add(event: string, callback: Callback): void {
    if (!this.listeners[event]) {
      this.listeners[event] = [callback];
      return;
    }
    this.listeners[event].push(callback);
  }
  remove(event: string, listener: Callback) {
    if (this.listeners[event]) {
      this.listeners[event] = this.listeners[event].filter(
        (c) => c !== listener
      );
    }
  }

  getEventListeners(event: string): Callback[] {
    if (this.listeners[event]) {
      return this.listeners[event];
    }
    return [];
  }

  getEventListener(event: string, callback: Callback): Callback | undefined {
    if (this.listeners[event]) {
      return this.listeners[event].find((c) => c === callback);
    }
    return;
  }
}

class EventEmitter {
  events: EventListenerContainer;
  constructor() {
    this.events = new EventListenerContainer();
  }

  addEventListener(event: string, callback: Callback): void {
    this.events.add(event, callback);
  }

  removeEventListener(event: string, listener: Callback): void {
    this.events.remove(event, listener);
  }

  on(event: string, callback: (...args: any) => void): void {
    this.addEventListener(event, callback);
  }

  emit(event: string, ...args: any): void {
    this.events.getEventListeners(event).forEach((listener) => {
      if (typeof listener === "function") {
        listener(...args);
      }
    });
  }
}

type RTCPeerConnectionConfig = {
  iceServers: { urls: string }[];
};
class WebRTC {
  peerConnection;
  eventEmitter;
  constructor(config: RTCPeerConnectionConfig) {
    this.eventEmitter = new EventEmitter();
    this.peerConnection = new RTCPeerConnection(config);

    this.peerConnection.ontrack = this.emitCall;

    socket.on("onicecandidate", async ({ candidate }: any) => {
      console.log("got candidate: ", { candidate });
      await this.addCandidate({ candidate });
    });

    socket.on("video:answer", async ({ answer, to, from }: any) => {
      console.log("got answer: ", { answer, to, from });
      await this.setRemoteAnswer(answer);
    });

    socket.on("video:offer", async ({ offer, to, from }: any) => {
      console.log("got offer: ", { offer, to, from });
      await this.acceptOffer({ offer, to, from });
    });
  }

  private async setRemoteAnswer({ answer }: any) {
    await this.peerConnection.setRemoteDescription(answer);
  }

  private async addCandidate({ candidate }: any) {
    await this.peerConnection.addIceCandidate(candidate);
  }

  private async acceptOffer({
    offer,
    from,
    to,
  }: {
    offer: any;
    from: string;
    to: string;
  }) {
    console.log("accept offer: ", { offer, to, from });
    await this.setOffer(offer);
    const answer = this.createAnswer();
    await this.setLocalAnswer(answer);
    this.emitAnswer(answer, from, to);
  }

  private async createAnswer() {
    return await this.peerConnection.createAnswer();
  }

  private emitAnswer(answer: any, from: string, to: string) {
    console.log("sending answer: ", { answer, to: from, from: to });
    socket.emit("video:answer", { answer, to: from, from: to });
  }
  private async setLocalAnswer(answer: any) {
    return this.peerConnection.setLocalDescription(answer);
  }
  private async setOffer(offer: any) {
    return this.peerConnection.setRemoteDescription(offer);
  }

  private emitCall(streams: any): void {
    this.eventEmitter.emit("call", streams);
  }

  private emitVideoOffer(offer: any, from: string, to: string) {
    console.log("sending offer: ", { offer, to, from });
    socket.emit("video:offer", { offer, from, to });
  }

  on(event: string, callback: Callback) {
    this.eventEmitter.addEventListener(event, callback);
  }

  private emitCandidate(candidate: any, from: string, to: string) {
    if (candidate) {
      console.log("sending candidate: ", { candidate });
      socket.emit("onicecandidate", {
        candidate,
        to,
        from,
      });
    }
  }

  async call(to: string, from: string, streams: any) {
    for (const track of streams.getTracks()) {
      this.peerConnection.addTrack(track, streams);
    }

    const offer = await this.peerConnection.createOffer();
    await this.peerConnection.setLocalDescription(offer);

    this.emitVideoOffer(offer, from, to);

    this.peerConnection.onicecandidate = ({ candidate }: any) => {
      this.emitCandidate(candidate, from, to);
    };
  }
}

export { WebRTC };
export type { RTCPeerConnectionConfig };
