import React, {
  useEffect,
  useLayoutEffect,
  useRef,
  useState
} from 'react'
import './App.css';
import {socketConnector} from "./socket/react-socket-connector/react-socket-connector";
import {useSubscription} from "./socket/use-subscription/useSubscription";

const openMediaDevices = async (constraints: any) => {
  return await navigator.mediaDevices.getUserMedia(constraints);
}

// async function getConnectedDevices(type) {
//   const devices = await navigator.mediaDevices.enumerateDevices();
//   console.log(devices)
//   return devices.filter(device => device.kind === type)
// }
//
// const videoCameras = getConnectedDevices('videoinput');
// videoCameras.then(v => {
//   console.log('Video: ', v)
// })
//
// const microphones = getConnectedDevices('audioinput');
// microphones.then(v => {
//   console.log('Mic: ', v)
// })

class SignalingChannel {
  id: string | null = null
  subscribers:{
    [key: string]: any
  }  = {
    // event:[callbacks]
  }

  register(id: string){
    this.id = id
    fetch('/init',{
      mode: 'no-cors',
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ id: id })
    })
      .then(res => res.json())
      .then(res=> {
        console.log('register: ', res)
    })
  }

  addEventListener(event: any, callback: any){
    if (!this.subscribers[event]){
      this.subscribers[event] = [callback]
      return
    }
    this.subscribers[event].push(callback)
  }

  removeEventListener(event: any, listener:any){
    if (!this.subscribers[event]){
      return
    }
    this.subscribers[event] = this.subscribers[event].filter((l:any) => l !== listener)
  }

  send(event: any){
    if (!this.subscribers[event.type]){
      // no subscriber for event.type found
      return
    }
    this.subscribers[event.type].forEach((subscriber: any) => {
      // call all subscribers for event.type
      if (typeof subscriber === "function"){
        subscriber(event.payload)
      }
    })
  }
}
const signalingChannel = new SignalingChannel();
signalingChannel.addEventListener('message', (message:any) => {
  // New message from remote client received
});

async function makeCall(): Promise<any> {
  const configuration = {
    iceServers: [
      {
        urls: "stun:192.168.2.120:3478",
        username: "username",
        credential: "password"
      }
    ]
  }
  const peerConnection = new RTCPeerConnection(configuration);
  signalingChannel.addEventListener('answer', async (message: any) => {
    if (message.payload.answer) {
      const remoteDesc = new RTCSessionDescription(message.payload.answer);
      await peerConnection.setRemoteDescription(remoteDesc);
    }
  });
  const offer = await peerConnection.createOffer();
  console.log(offer)
  await peerConnection.setLocalDescription(offer);
  signalingChannel.send({type: 'offer', payload: { offer }});
}


function App(props: any) {
  const [name, setName] = useState('')
  const [confirmed, setConfirmed] = useState(false)


  useSubscription('events', (d) => {
    console.log(d)
  })

  const ownVideoRef = useRef<any>()
  useLayoutEffect(() => {
    const connect = async () => {
      try {
        // const constraints = {'video': true, 'audio': true};
        // const stream = await openMediaDevices(constraints);
        //
        // ownVideoRef.current.srcObject = stream;
      } catch (e){
        console.error(e)
      }
    }
    // connect()
  }, [])

  useEffect(() => {
    if (confirmed){
      signalingChannel.register(name)
    }
  }, [confirmed])

  return (
    <div className="App">
      {
      confirmed && (
        <div>
          <div>
            <video id={'video'} ref={ownVideoRef} autoPlay playsInline controls={false} style={{
              height: "300px",
              width: '300px'
            }}/>
          </div>
          <div>
            <button onClick={() => makeCall().then(res => {
              console.log(res)
            })}>CALL</button>
          </div>
          <div>
            <button>ANSWER</button>
          </div>
        </div>
      )
    }
    {!confirmed && (
      <div>
        {/*<NameInput value={name} onChange={(e) => setName(e.target.value) } onConfirm={()=> setConfirmed(true) }/>*/}
        <button onClick={() => props.sendEvents()}>click</button>
      </div>
    )}
    </div>
  );
}

const AppWithSocket = socketConnector((dispatch: any)=>({
  sendEvents: () => {
    dispatch({type: 'events', payload: {data: 'fromApp'}})
  }
}))(App)

export default AppWithSocket;
