class HttpService {
  constructor(private readonly options: { path: string }) {}

  async post(url: string, data: any) {
    return fetch(`${this.options.path}${url}`, {
      headers: {},
      method: "POST",
      body: JSON.stringify(data),
    }).then((res) => res.json());
  }
}
export { HttpService };
