import React, { useContext } from "react";
import { createContext } from "react";
import { useLocalStorage } from "../../hooks/use-local-storage/useLocalStorage";
import { socketConnector } from "../../socket/react-socket-connector/react-socket-connector";

enum AuthenticationActionTypes {
  login = "login",
  logout = "logout",
}

type LoginAction = {
  type: AuthenticationActionTypes.login;
  payload: { name: string; password: string };
};
type LogoutAction = {
  type: AuthenticationActionTypes.logout;
};

type AuthenticationActions = LoginAction | LogoutAction;

type Authentication = { isLoggedIn: boolean; key: string; name: string };
const defaultAuthentication: Authentication = {
  isLoggedIn: false,
  key: "",
  name: "",
};

type AuthenticationDispatch = (
  action: AuthenticationActions | ((action: AuthenticationActions) => void)
) => void;

const AuthValueContext = createContext<Authentication>({
  ...defaultAuthentication,
});

const AuthDispatchContext = createContext<AuthenticationDispatch>(() => {});

const ValueProvider: any = AuthValueContext.Provider;
ValueProvider.displayName = "ValueProvider";

const DispatchProvider: any = AuthDispatchContext.Provider;
DispatchProvider.displayName = "DispatchProvider";

const useAuthentication = (): [Authentication, AuthenticationDispatch] => {
  return [
    useContext<Authentication>(AuthValueContext),
    useContext<AuthenticationDispatch>(AuthDispatchContext),
  ];
};

const _AuthenticationProvider = ({ children, login: socketLogin }: any) => {
  const [auth, setValue] = useLocalStorage<Authentication>("login", {
    ...defaultAuthentication,
  });

  const isAuthenticated = (): boolean => {
    return false;
  };

  const login = (name: string, password: string) => {
    socketLogin(name, password).then((res: any) => {
      console.log(res);
      setValue((val) => ({ ...val, isLoggedIn: true, name: name }));
    });
  };

  const logout = () => {
    setValue((val) => ({ ...val, isLoggedIn: false, name: "", key: "" }));
  };

  const dispatch = (action: AuthenticationActions) => {
    const _switch = {
      [AuthenticationActionTypes.login]: (action: LoginAction) =>
        login(action.payload.name, action.payload.password),
      [AuthenticationActionTypes.logout]: (action: LogoutAction) => logout(),
    };
    // @ts-ignore
    _switch[action.type]?.(action);
  };

  return (
    <ValueProvider value={auth}>
      <DispatchProvider value={dispatch}>{children}</DispatchProvider>
    </ValueProvider>
  );
};

const AuthenticationProvider = socketConnector((dispatch) => {
  return {
    login: (name: string, password: string) =>
      dispatch({
        type: "login",
        payload: { name, password },
      }),
  };
})(_AuthenticationProvider);

export { AuthenticationProvider, useAuthentication, AuthenticationActionTypes };
