import React from "react";
import { useUserList } from "./hooks/use-user-list/useUserList";
import styled from "styled-components";
import { useAuthentication } from "../../../../context/authentication/Authentication.context";

const Ul = styled.ul`
  margin: 0;
  padding: 0;
`;

const UserList = ({
  onVideo,
  onAudio,
}: {
  onVideo: (name: string) => void;
  onAudio: (name: string) => void;
}) => {
  const userList = useUserList();
  const [{ name }] = useAuthentication();

  const removeSelf = ({name: userName}:{name: string})=> name !== userName

  return (
    <div>
      <Ul>
        {userList.filter(removeSelf).map(({ name }, idx) => {
            return (
              <li key={`${name}-${idx}`}>
                <span>
                  <span>{name}</span>
                  <span>
                    <button onClick={() => onVideo(name)}>Video</button>
                    <button onClick={() => onAudio(name)}>Audio</button>
                  </span>
                </span>
              </li>
            );
        })}
      </Ul>
    </div>
  );
};

export { UserList };
