import {useEffect, useState} from "react";
import {socket} from "../socket";

function useEmitter<T = any>(){
  const [state, setState] = useState<T>()

  const emit = (type: string, payload: any) => {
    socket.emit(type, payload, (...args: any) => {
      // @ts-ignore
      setState(...args)
    })
  }

  return [state]
}
export {useEmitter}
