import React from 'react';
import {createContext} from "react";
import {useLocalStorage} from "../../hooks/use-local-storage/useLocalStorage";

const AuthValueContext = createContext({

})

const AuthDispatchContext = createContext(function noop(){})

const ValueProvider: any = AuthValueContext.Provider
ValueProvider.displayName = 'ValueProvider'

const DispatchProvider: any = AuthDispatchContext.Provider
DispatchProvider.displayName = 'DispatchProvider'

type LoginAction = {type: 'login', payload: {name: string, password: string}}
type AuthenticationActions = LoginAction

const AuthenticationProvider = ({children}: any) => {
  const [{ isLoggedIn }, setValue] = useLocalStorage<{isLoggedIn: boolean}>('login', { isLoggedIn: false })

  const isAuthenticated = (): boolean => {
    return false
  }

  const login = (name:string, password: string) => {
    setValue({isLoggedIn: true})
  }

  const dispatch = (action: AuthenticationActions) => {
    switch (action.type){
      case 'login':{
        login(action.payload.name, action.payload.password)
        break;
      }
      default:{
        break
      }
    }
  }

  return (
    <ValueProvider value={{ isLoggedIn }}>
      <DispatchProvider value={dispatch}>
        {children}
      </DispatchProvider>
    </ValueProvider>
  );
};

export { AuthenticationProvider };
