import React from 'react'
import {socket} from "../socket";

function socketConnector(socketInstanceMapper){
  const dispatch = ( action ) => {
    if (typeof action === "function"){
      const val = action(dispatch)
      socket.emit(...Object.values(val))
    } else {
      socket.emit(...Object.values(action))
    }
  }
  const mappedSocket = socketInstanceMapper(dispatch)

  const WithSocket = (Component) => {
    WithSocket.displayName = `withSocket(${Component.displayName || 'Component'})`
    return (props) => <Component {...props} {...mappedSocket}/>
  }

  return WithSocket
}

export { socketConnector }
