import React, { useEffect, useLayoutEffect, useRef, useState } from "react";
import { useAuthentication } from "../../context/authentication/Authentication.context";
import { socket } from "../../socket/socket";
import { socketConnector } from "../../socket/react-socket-connector/react-socket-connector";
import { RTCPeerConnectionConfig, WebRTC } from "../chat/WebRTC";
import { UserList } from "../chat/components/user-list/UserList";
import { LogoutButton } from "../chat/components/logout-button/logout-button";
import { SOCKET_ADDRESS } from "../../evn";

const openMediaDevices = async (constraints: any) => {
  return await navigator.mediaDevices.getUserMedia(constraints);
};

const connectVideo = (videoRef: any, stream: any) => {
  videoRef.current.srcObject = stream;
};

const webRTC = new WebRTC({
  iceServers: [{ urls: "stun:stun.1.google.com:19302" }],
  socket: {
    url: SOCKET_ADDRESS,
    options: {
      path: "/socket",
    },
  },
});

const _WebRTCChat = ({ emitVideoOffer }: any) => {
  const [{ name: currentUserName }] = useAuthentication();
  const ownStream = useRef<any>();
  const ownVideoRef = useRef<any>();
  const foreignVideoRef = useRef<any>();

  useLayoutEffect(() => {
    webRTC.on("call", async (call) => {
      const stream = await openMediaDevices({
        video: true,
        audio: true,
      });
      call.answer(stream);
    });
  }, []);

  const onVideo = async (name: string) => {
    const call = webRTC.call(name);
    call.on("stream", ({ stream }) => {
      // handle foreign stream
    });
  };

  const onAudio = (name: string) => {};

  return (
    <div>
      <UserList onAudio={onAudio} onVideo={onVideo} />
      <LogoutButton />
      <div>
        <div>
          <div>local</div>
          <div>
            <video
              id={"ownVideo"}
              ref={ownVideoRef}
              autoPlay
              playsInline
              controls={false}
              style={{
                height: "300px",
                width: "300px",
              }}
            />
          </div>
        </div>
        <div>
          <div>remote</div>
          <div>
            <video
              id={"foreignVideo"}
              ref={foreignVideoRef}
              autoPlay
              playsInline
              controls={false}
              style={{
                height: "300px",
                width: "300px",
              }}
            />
          </div>
        </div>
      </div>
    </div>
  );
};
const WebRTCChat = socketConnector((dispatch) => {
  return {
    emitVideoOffer: (offer: any, from: string, to: string) =>
      dispatch({ type: "video:offer", offer, from, to }),
  };
})(_WebRTCChat);
export { WebRTCChat };
